"use client";

import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Store, MapPin, Loader2, CheckCircle2, AlertCircle } from "lucide-react";
import { supabase } from "@/lib/supabase";

interface Location {
    name: string; // resource name
    title: string;
    storeCode?: string;
    metadata?: {
        placeId?: string;
        mapsUri?: string;
    };
}

export default function GbpConnect() {
    const [loading, setLoading] = useState(false);
    const [locations, setLocations] = useState<Location[]>([]);
    const [isConnected, setIsConnected] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const pollInterval = useRef<NodeJS.Timeout | null>(null);

    useEffect(() => {
        checkConnection();

        // Listen for message from popup
        const handleMessage = (event: MessageEvent) => {
            if (event.origin !== window.location.origin) return;
            if (event.data === "gbp-connected") {
                checkConnection();
            }
        };

        window.addEventListener("message", handleMessage);
        return () => window.removeEventListener("message", handleMessage);
    }, []);

    // Cleanup polling on unmount
    useEffect(() => {
        return () => {
            if (pollInterval.current) clearInterval(pollInterval.current);
        }
    }, []);

    const checkConnection = async () => {
        try {
            const { data: { session } } = await supabase.auth.getSession();

            if (!session) return;

            const response = await fetch("/api/locations", {
                headers: {
                    Authorization: `Bearer ${session.access_token}`,
                },
            });

            if (response.ok) {
                const data = await response.json();
                if (data.locations) {
                    setLocations(data.locations);
                    setIsConnected(true);
                    // Stop polling if we found locations/connection
                    if (pollInterval.current) {
                        clearInterval(pollInterval.current);
                        pollInterval.current = null;
                    }
                    setLoading(false); // Ensure loading is off
                }
            } else {
                const data = await response.json();
                if (data.error && data.error.includes("No tokens found")) {
                    setIsConnected(false);
                }
            }

        } catch (error) {
            console.error("Error checking connection:", error);
        }
    };

    const handleConnect = async () => {
        try {
            setLoading(true);
            setError(null);
            const response = await fetch("/api/gbp-connect");
            const data = await response.json();

            if (data.url) {
                const width = 500;
                const height = 600;
                const left = window.screen.width / 2 - width / 2;
